import { FaMinusCircle, FaPlusCircle, FaTrashAlt } from 'react-icons/fa'
import Swal from 'sweetalert2';
import { priceFormat } from '../../utils/priceFormat';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { CartContext } from '../../contexts';
import Menu from '../../components/Menu';

const Cart = () => {

  const navigate = useNavigate();
  const { cart, handleChangeQuantity, removeItem } = useContext(CartContext);

  const total = priceFormat(cart.reduce((acc, currentValue) => {
    return acc + (currentValue.product.price * currentValue.quantity)
  }, 0))

  console.log(cart)
  return (
    <>
    <Menu />

      <div className='main-container'>
        <table className='table-cart' data-testid="table-cart">
        <thead>
          <tr>
            <th>#</th>
            <th>Produto</th>
            <th>Quantidade</th>
            <th>SubTotal</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.itemId}>
              <td>
                <img className='table-image' src={item.product.image} alt={item.product.name} />
              </td>
              <td>{item.product.name}</td>
              <td>
                <FaMinusCircle
                  size={18}
                  color="#9721BD"
                  className='margin-button'
                  onClick={() => handleChangeQuantity( item.quantity - 1, item.itemId)}
                />

              {item.quantity}
              <FaPlusCircle
                size={18}
                color="#9721BD"
                className='margin-button'
                onClick={() => handleChangeQuantity(item.quantity + 1, item.itemId)}
              />
              </td>

              <td>{item.subTotalFormatted}</td>
              <td>
                <FaTrashAlt size={22} color='#9721BD' onClick={() => {
                  Swal.fire({
                    title: 'Deseja realmente remover esse item ?',
                    showCancelButton: true,
                    confirmButtonText: 'Sim, desejo !',
                    cancelButtonText: `Cancelar`,
                  }).then((result) => {
                    if (result.isConfirmed) {
                      removeItem(item.id)
                    }
                  })
                }} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className='footer-cart'>
          <h1>Total: {total}</h1>
        <button className='book-button' onClick={() => navigate('/checkout')}>Finalizar compra</button>
      </div>
    </div>
    </>
  )
}

export default Cart
